import styled from "styled-components"
import BoxShadow from "./StyleComponents";
import { useEffect, useState } from "react";

const COLORL = {
    Primary: '#5daece',
    PrimaryLight: '#8cc9e1',
    PrimaryDark: '3b85a3',
    Secondary: '#bad2c6',
    SecondaryLight: '#d7efe3',
    SecondaryDark: '#80a391',
    White: '#ffffff',
    Black: '#000000',
    GSFA: '#fafafa',
    GSF5: '#f5f5f5',
    GSF0: '#f0f0f0',
    GSD9: '#d9d9d9',
    GSBF: '#bfbfbf',
    GS8c: '#8c8c8c',
    GS59: '#595959',
    GS43: '#434343',
    GS26: '#262626',
    GS14: '#141414',
    Red: '#ec132E',
    Green: '#3ac73b',
}

const CalendarContainer = styled.div`
    background-color: ${COLORL.White};
    width: 33.2rem;
    height: 40.1rem;
    border-radius: 0.8rem;
    ${BoxShadow}
    grid-column: 1/2;
    grid-row: 1/2;
    background-size: cover;
    background-repeat: no-repeat;
    overflow: hidden;
`;
const UpCalendar = styled.div`
    background-color: ${COLORL.White};
    width: 33.2rem;
    height: 29.6rem; 
    padding: 1.6rem;
`;
const UpCalTitle = styled.div`
    width: 30rem;
    height: 2.4rem;
    background-color: ${COLORL.Primary};
    margin-bottom: 1.6rem;
    font-size: 1.6rem;
`;
const CalListBox = styled.div`
    width: 30rem;
    height: 22.4rem;
    // background-color: ${COLORL.Primary};
`;

//달력 리스트입니다.
const DayItemContainer = styled.li`
    display: inline-block;
    width: 4.286rem;
    height: 3.2rem;
    margin: 0.4rem 0;
    text-align: center;
    line-height: 3.2rem;
    font-size: 1.6rem;
    position: relative;
`;
//달력 list 선택 css 기능 
const ItemLeftBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${COLORL.Primary};
    z-index: 10;
`;
const ItemRightBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${COLORL.Primary};
    z-index: 10;
`;
const ItemCircle = styled.div`
    width: 3.2rem;
    height: 3.2rem;
    border-radius: 9999px;
    background-color: ${props => props.color};
    position: absolute;
    top: 0;
    left: 0.586rem;
    z-index: 11;
    overflow: hidden;
    cursor: pointer;

    &:hover {
        background-color: ${COLORL.Green};
    }
`;
const Span = styled.span`
    -webkit-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
`;
const Week = styled.div`
    width: 4.286rem;
    height: 2.4rem;
    display: inline-block;
    text-align: center;
`;
// const pickHandle = [];



const pickCon = [];

//메인 
function CalendarBox () {
    const dataCal = new Date();
    const year = dataCal.getFullYear();
    const month = dataCal.getMonth();
    const totalMonthDay = new Date(year, month, 0).getDate(); // 1 ~ 31
    const firstDayWeek = new Date(year, month).getDay(); //0 ~ 6



    function CalList ({ value, listColor }) {
        const [pickColor, setPickColor] = useState(COLORL.Red);

        function colorHandler () {
            let toggle = false;
            if (pickCon.length === 0) {
                for (let i = 0; i < pickCon.length ; i++) {
                    if (pickCon[i] === value) {
                        toggle = true;
                    };
                }
                if(!toggle) {
                    pickCon.push(value);
                    setPickColor(COLORL.Green);
                } else if (toggle) {
                    setPickColor(COLORL.Red);
                    toggle = false;
                }
            } else if (pickCon.length === 1) {
                for (let i = 0; i < pickCon.length ; i++) {
                    if (pickCon[i] === value) {
                        toggle = true;
                    };
                }
                if(!toggle) {
                    pickCon.push(value);
                    setPickColor(COLORL.Green);
                } else if (toggle) {
                    setPickColor(COLORL.Red);
                    pickCon.splice(0);
                    toggle = false;
                }
                //선택일정 가운데 색상변경 토글


            } else if (pickCon.length === 2) {
                for(let i = 0; pickCon.length > i ; i++) {
                    if (pickCon[i] === value){
                        pickCon.splice(i, 1);
                        setPickColor(COLORL.Red);
                    }
                }
            }
            console.log(pickCon);
        }
        return (
            <>
                <DayItemContainer key={value} id={`li${value}`}>
                    <ItemLeftBox listColor={listColor} id={`left${value}`}/>
                        <ItemCircle id={`cal${value}`} key={value} onClick={colorHandler} color={pickColor}>
                            <Span>{value}</Span>
                        </ItemCircle>
                    <ItemRightBox listColor={listColor} id={`right${value}`}/>
                </DayItemContainer>
            </>
        )
    };

    const days = [];
    // function renderList () {
    //     days.splice(0);
    //     for (let i = 0; i < firstDayWeek ; i++ ) {
    //         //빈칸 렌더링
    //         days.push(<CalList key={`empty${i}`}/>);
    //     }
    //     for (let i = 1; i <= totalMonthDay ; i++) {
    //         //day 렌더링
    //         days.push(<CalList key={i} value={i} />)
    //     }

    //     return days;
    // }
    useEffect(() => {
        days.splice(0);
        for (let i = 0; i < firstDayWeek ; i++ ) {
            //빈칸 렌더링
            days.push(<CalList key={`empty${i}`}/>);
        }
        for (let i = 1; i <= totalMonthDay ; i++) {
            //day 렌더링
            days.push(<CalList key={i} value={i} />)
        }

        return days;
    }, [pickCon])

    return (
        <CalendarContainer>
            <UpCalendar>
                <UpCalTitle>MAY 2023</UpCalTitle>
                <CalListBox>
                    <Week>S</Week>
                    <Week>M</Week>
                    <Week>T</Week>
                    <Week>W</Week>
                    <Week>T</Week>
                    <Week>F</Week>
                    <Week>S</Week>
                    {days}
                </CalListBox>
            </UpCalendar>
        </CalendarContainer>
    )
}

export default CalendarBox;
