import styled, { keyframes } from "styled-components"
import COLOR from "./COLOR";



const ProgressBar = styled.div`
    width: 62rem;
    height: 3.2rem;
    border-radius: 999rem;
    background-color: ${COLOR.GSF0};
    box-shadow: inset 0px 3px 7px rgba(0, 0, 0, 0.2);
    position: relative;
`;

function ProgressTitleCom ({ color }) {
    const colorA = color;
    const ProgressTitle = styled.div`
    width: 12rem;
    height: 3.2rem;
    border-radius: 999rem;
    background-color: #7986cc;
    margin-right: 1.5rem;
    text-align: center;
    font-size: 1.6rem;
    color: ${COLOR.colorA};
    line-height: 3.2rem;

`;
}


const ProgressBox = styled.div`
    width: 75.5rem;
    height: 3.2rem;
    display: flex;
    flex-direction: row;
    margin-bottom: 1.2rem;
`;



function ProgressGraphCom ({ value }) {
    const ani = keyframes`
        0% {
            width: 0;
        }
        100% {
            width: ${value * 2}rem;
        }

    `
    const ProgressGraph = styled.div`
        width: ${value * 2}rem;
        height: 3.2rem;
        position: absolute;
        top: 0;
        left: 0;
        border-radius: 999rem;
        background-color: ${COLOR.Primary};
        text-align: right;
        color: ${COLOR.White};
        font-size: 1.6rem;
        line-height: 3.2rem;
        padding-right: 1rem;
        animation: ${ani} 0.5s both linear;
    `;
    return (
        <ProgressGraph>{value}</ProgressGraph>
    )
}
function ProgressBarContainer ({ value, name, color }) {
    return (
        <ProgressBox>
            <ProgressTitleCom color={color}>{name}</ProgressTitleCom>
            <ProgressBar>
                <ProgressGraphCom value={value}/>
            </ProgressBar>
        </ProgressBox>
    )
}

export default ProgressBarContainer;
