import InputLogin from './InputLogin';
import './loginBox.css';
import LoginButton from './LoginButton';
import Logo from './Logo';
import { useState } from 'react';


function LoginBox() {
    const [cancelImgMoveId, setCancelImgMoveId] = useState(0);
    const [cancelImgMovePw, setCancelImgMovePw] = useState(0);

    function cancelImgFocusId() {
        setCancelImgMoveId(1);
    }

    function cancelImgBlurId() {
        setCancelImgMoveId(0);
    }
    function cancelImgFocusPw() {
        setCancelImgMovePw(1);
    }

    function cancelImgBlurPw() {
        setCancelImgMovePw(0);
    }

    return (
        <div className="login-container">
            <h1>오늘의 나를 담아 내일로,</h1>
            <Logo />
            <InputLogin type='text' onBlur={cancelImgBlurId} onFocus={cancelImgFocusId} value={cancelImgMoveId}>아이디</InputLogin>
            <InputLogin type='password' onBlur={cancelImgBlurPw} onFocus={cancelImgFocusPw} value={cancelImgMovePw}>비밀번호</InputLogin>
            <LoginButton />
        </div>
    )
}

export default LoginBox;
