import styled, { keyframes } from "styled-components";
import COLOR from "./COLOR";
import BoxShadow from "./StyleComponents";

const MySkillContainer = styled.div`
    width: 44.8rem;
    height: 19.4rem;
    border-radius: 0.8rem;
    ${BoxShadow}
    background-color: ${COLOR.White};
    grid-column: 1/4;
    grid-row: 3/4;
    padding: 1.6rem;
`;

const H2 = styled.h2`
    font-size: 20px;
    font-weight: 400;
    margin: 0;
`;

const GraphContainer = styled.div`
    width: 6.5rem;
    height: 6.5rem;
    // background-color: ${COLOR.GSF0};
    position: relative;
    margin: 0 auto;
    margin-bottom: 1.5rem;
`;

const GraphCircle = styled.circle`
        fill: transparent;
        stroke: ${COLOR.Primary};
        stroke-width: 1.5rem;
        -webkit-text-stroke-dasharray: 105px;
        stroke-dasharray: 160;
        stroke-dashoffset: ${props => props.calcValue};
        stroke-linecap: round;
        transform: rotate(-90deg);
        transform-origin: center center;
        animation: ${props => props.CircleFrame} 2s both linear;
    `;

function ProgressCircle ({ value }) {
    const calcValue = (100 - value) * 1.6;
    const CircleFrame = keyframes`
        0%{
            stroke-dashoffset: 160;
        }
        100% {
            stroke-dashoffset: ${calcValue};
        }
    `

    return(
        <GraphCircle CircleFrame={CircleFrame} calcValue={calcValue} cx='32.5' cy='32.5' r='26'/>
    )
}

const ValueG = styled.div`
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);   
    `;
    const GraphTitle = styled.div`
        font-size: 1.6rem;
        text-align: center;
    `;
    const Div = styled.div`
        display: flex;
        flex-direction: column;
    `;
function SkillGraph ({ value, title }) {

    return(
        <Div>
        <GraphContainer>
            <svg viewBox="0 0 65 65">
                <ProgressCircle value={value}/>
            </svg>
            <ValueG>{value}%</ValueG>
        </GraphContainer>
        <GraphTitle>{title}</GraphTitle>
        </Div>
    )
}
const Float = styled.div`
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        margin-top: 2rem;
    `;
function MySkill({ activeValue }) {
    const valueProp = activeValue;

    return (
        <MySkillContainer>
            <H2>My Skills</H2>
            <Float>
                <SkillGraph value={valueProp[1].skillCountA} title={'글자넘칠...'}></SkillGraph>
                <SkillGraph value={activeValue} title={'포토샵'}></SkillGraph>
                <SkillGraph value={activeValue} title={'일러스트레이터'}></SkillGraph>
                <SkillGraph value={activeValue} title={'워드'}></SkillGraph>
            </Float>
        </MySkillContainer>
    )
}

export default MySkill;
