import './startWave.css'
import UserInfo from './UserInfo';
import './Main.css';
import logoImg from '../../image/LogoImage.png';
import './Button.css';
import WaveButton from './ButtonStyle';
import NavBox from './NavBox';
import CalendarBox from './CalendarBox';
// import Prac from './Prac';
import MonthRecord from './MonthRecord';
import SkillBox from './SkillBox';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

const mainInfoA = [
    {
        title: 'userinfo',
        name: '오주연',
        userNumber: '22201011',
        nickName: 'dongdongthecat',
    },
    {
        title: 'MySkills',
        skillA: '글자넘칠..',
        skillB: '포토샵',
        skillC: '일러스트레이터',
        skillD: '워드',
        skillCountA: 0,
        skillCountB: 50,
        skillCountC: 80,
        skillCountD: 100,
    },
    {
        title: 'activestats',
        active: 9,
        record: 27,
        capacity: 5,
    },
    {
        title: 'comment',
        comment: '호락호락하지 않음',
    },
    {
        title: 'strong',
        A: '첫번째',
        B: '두번째',
        C: '세번째',
        D: '네번째',
        E: '다섯번째',
    },
    {
        title: 'monthrecord',
        aa: 12,
        bb: 26,
        cc: 5,
        dd: 0,
        ee: 10,
        ff: 5,
    }
];

function Main() {
    const [mainInfo, setMainInfo] = useState(mainInfoA);
    const movePage = useNavigate();
    function clickLogout () {
        alert('로그아웃.');
        fetch("http://43.200.9.209:8000/logout", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            credentials : "include",
        })
        .then((res) => res.json())
        .then((res) => {
            movePage('/');
        })
        .catch((error) => {
            console.error(new Error("로그아웃 중 에러 발생"));
        });
    }
    const [infoToggle, setInfoToggle] = useState(false);
    function commentClick() {
        setInfoToggle(infoToggle => !infoToggle);
    }

    useEffect(() => {
        const mainReq = async () => {
            try{
                const response = await fetch(url);
                const data = await response.json();
                setMainInfo(data);
            }catch(error){
                console.error(error);
            }
        }
        mainReq();
    }, []);


    const logo = logoImg;
    return (
    <>
        <div className='start'></div>
        <div className='main-container'>
            <div className='left-container'>
                <img className='main-logo-img' src={logo} alt="Fullio Logo" />
                <NavBox />
                <WaveButton onClick={clickLogout}>로그아웃</WaveButton>
            </div>
            <div className='cneter-container'>
                <CalendarBox></CalendarBox>
                <SkillBox activeValue={mainInfo}></SkillBox>
                <MonthRecord value={mainInfo}></MonthRecord>

            </div>
            <div className='right-container'>
                <UserInfo onClick={commentClick} commentPro={mainInfo[3].comment} infoToggle={infoToggle}/>
            </div>
        </div>
    </>
    )
}

export default Main;
