import styled from "styled-components"
import BoxShadow from "./StyleComponents";
import COLOR from "./COLOR";
import { useState } from "react";

const CalendarContainer = styled.div`
    background-color: ${COLOR.White};
    width: 33.2rem;
    height: 40.1rem;
    border-radius: 0.8rem;
    ${BoxShadow}
    grid-column: 1/2;
    grid-row: 1/2;
    background-size: cover;
    background-repeat: no-repeat;
    overflow: hidden;
`;
const UpCalendar = styled.div`
    background-color: ${COLOR.White};
    width: 33.2rem;
    height: 29.6rem;
    padding: 1.6rem;
`;
const UpCalTitle = styled.div`
    width: 30rem;
    height: 2.4rem;
    background-color: ${COLOR.Primary};
    margin-bottom: 1.6rem;
    font-size: 1.6rem;
`;
const CalListBox = styled.div`
    width: 30rem;
    height: 22.4rem;
    // background-color: ${COLOR.Primary};
`;

//달력 리스트입니다.
const DayItemContainer = styled.li`
    display: inline-block;
    width: 4.286rem;
    height: 3.2rem;
    margin: 0.4rem 0;
    text-align: center;
    line-height: 3.2rem;
    font-size: 1.6rem;
    position: relative;
`;
//달력 list 선택 css 기능 
const ItemLeftBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${COLOR.Primary};
    z-index: 10;
`;
const ItemRightBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${COLOR.PrimaryLight};
    z-index: 10;
`;
const ItemCircle = styled.div`
    width: 3.2rem;
    height: 3.2rem;
    border-radius: 9999px;
    background-color: ${props => props.color};
    position: absolute;
    top: 0;
    left: 0.586rem;
    z-index: 11;
    overflow: hidden;
    cursor: pointer;

    &:hover {
        background-color: ${COLOR.Green};
    }
`;
const Span = styled.span`
    -webkit-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
`;
const Week = styled.div`
    width: 4.286rem;
    height: 2.4rem;
    display: inline-block;
    text-align: center;
`;
// const pickHandle = [];


function CalList ({ value, color, onClick }) {
    // const [pickColor, setPickColor] = useState(COLOR.Red);
    // function colorHandler () {
    //     if (pickColor === COLOR.Red){
    //         //핸들러 중복 확인
    //         let maphandle = false;
    //         if(pickCon.length >= 2 ) {
    //             pickCon.push(value);
    //             pickCon.splice(0, 2);
    //         } else {
    //             for(let i = 0; i < pickCon.length ; i++) {
    //                 if(pickCon[i] === value){
    //                     maphandle = true;
    //                 }
    //             }
    //             if (!maphandle) {
    //                 pickCon.push(value);
    //             }
    //         }

    //         setPickColor(COLOR.Green);
    //         console.log(pickCon);
    //     } else {
    //         setPickColor(COLOR.Red);
    //     }
    // }

    return (
        <>
            <DayItemContainer key={value} id={`li${value}`}>
                <ItemLeftBox />
                    <ItemCircle id={`cal${value}`} key={value} onClick={onClick} color={color}>
                        <Span>{value}</Span>
                    </ItemCircle>
                <ItemRightBox />
            </DayItemContainer>
        </>
    )
}


//메인 
function CalendarBox () {
    const dataCal = new Date();
    const year = dataCal.getFullYear();
    const month = dataCal.getMonth();
    const totalMonthDay = new Date(year, month, 0).getDate(); // 1 ~ 31
    const firstDayWeek = new Date(year, month).getDay(); //0 ~ 6
    const pickCon = [];

    const [pickColor, setPickColor] = useState(COLOR.Red);

    function colorHandler ( value, pickColor ) {
        if (pickColor === COLOR.Red){
            //핸들러 중복 확인
            let maphandle = false;
            if(pickCon.length >= 2 ) {
                pickCon.push(value);
                pickCon.splice(0, 2);
                console.log(pickCon);
            } else {
                for(let i = 0; i < pickCon.length ; i++) {
                    if(pickCon[i] === value){
                        maphandle = true;
                    }
                }
                if (!maphandle) {
                    pickCon.push(value);
                }
            }

            setPickColor(COLOR.Green);
            console.log(pickCon);
        } else {
            setPickColor(COLOR.Red);
        }
    }


    function renderList () {

        // const days = [];
        // for (let i = 0; i < firstDayWeek ; i++ ) {
        //     //빈칸 렌더링
        //     days.push(<CalList key={`empty${i}`}/>);
        // }
        // for (let i = 1; i <= totalMonthDay ; i++) {
        //     //day 렌더링
        //     days.push(<CalList key={i} value={i} color={pickColor} onClick={colorHandler}/>)
        // }
        // return days;
        return () => {
            for (let i = 0; i < firstDayWeek ; i++ ) {
                //빈칸 렌더링
                <CalList key={`empty${i}`}/>
            }
            for (let i = 1; i <= totalMonthDay ; i++) {
                //day 렌더링
                <CalList key={i} value={i} color={pickColor} onClick={colorHandler}/>
            }};
    }



    return (
        <CalendarContainer>
            <UpCalendar>
                <UpCalTitle>MAY 2023</UpCalTitle>
                <CalListBox>
                    <Week>S</Week>
                    <Week>M</Week>
                    <Week>T</Week>
                    <Week>W</Week>
                    <Week>T</Week>
                    <Week>F</Week>
                    <Week>S</Week>
                    {renderList()}
                </CalListBox>
            </UpCalendar>
        </CalendarContainer>
    )
}

export default CalendarBox;
