import styled from "styled-components";
import COLOR from "./COLOR";

const Comment = styled.span`

font-size: 1.4rem
color: ${COLOR.Black};
`

function InfoComment ({ value }) {
    const commentContent = `"${value}"`;
    return (
        <div className='info-comment-container'>
            {commentContent}
        </div>
    )
}

export default InfoComment;

