function InfoComment ({ value, onClick, toggle }) {
    const commentContent = `"${value}"`;
    return (
        <div onClick={onClick} className='info-comment-container'>
            {toggle ? <input>{commentContent}</input>:{commentContent}}
        </div>
    )
}

export default InfoComment;
