import InputLogin from './InputLogin';
import './loginBox.css';
import LoginButton from './LoginButton';
import Logo from './Logo';
import { useState } from 'react';


function LoginBox() {
    const [cancelImgMoveId, setCancelImgMoveId] = useState(0);
    const [cancelImgMovePw, setCancelImgMovePw] = useState(0);
    const [textId, setTextId] = useState('');
    const [textPw, setTextPw] = useState('');

    function cancelImgFocusId() {
        setCancelImgMoveId(1);
    }

    function cancelImgBlurId() {
        setCancelImgMoveId(0);
    }
    function cancelImgFocusPw() {
        setCancelImgMovePw(1);
    }

    function cancelImgBlurPw() {
        setCancelImgMovePw(0);
    }

    function resetTextId(e) {
        const inputText = e.target.value;
        setTextId(inputText);
    }
    function resetTextPw(e) {
        const inputText = e.target.value;
        setTextPw(inputText);
    }

    function clearTextId(){
        console.log('clear!');
    }

    return (
        <div className="login-container">
            <h1>오늘의 나를 담아 내일로,</h1>
            <Logo />
            <InputLogin type='text' onClick={clearTextId} onBlur={cancelImgBlurId} onFocus={cancelImgFocusId} value={cancelImgMoveId} text={textId} onChange={resetTextId}>아이디</InputLogin>
            <InputLogin type='password' onClick={clearTextId} onBlur={cancelImgBlurPw} onFocus={cancelImgFocusPw} value={cancelImgMovePw} text={textPw} onChange={resetTextPw}>비밀번호</InputLogin>
            <LoginButton />
        </div>
    )
}

export default LoginBox;
