import styled from "styled-components"
import BoxShadow from "./StyleComponents";
import { useState } from "react";
import COLOR from "./COLOR";

const COLORL = {
    Primary: '#5daece',
    PrimaryLight: '#8cc9e1',
    PrimaryDark: '3b85a3',
    Secondary: '#bad2c6',
    SecondaryLight: '#d7efe3',
    SecondaryDark: '#80a391',
    White: '#ffffff',
    Black: '#000000',
    GSFA: '#fafafa',
    GSF5: '#f5f5f5',
    GSF0: '#f0f0f0',
    GSD9: '#d9d9d9',
    GSBF: '#bfbfbf',
    GS8c: '#8c8c8c',
    GS59: '#595959',
    GS43: '#434343',
    GS26: '#262626',
    GS14: '#141414',
    Red: '#ec132E',
    Green: '#3ac73b',
}

const CalendarContainer = styled.div`
    background-color: ${COLORL.White};
    width: 33.2rem;
    height: 40.1rem;
    border-radius: 0.8rem;
    ${BoxShadow}
    grid-column: 1/2;
    grid-row: 1/2;
    background-size: cover;
    background-repeat: no-repeat;
    overflow: hidden;
`;
const UpCalendar = styled.div`
    background-color: ${COLORL.White};
    width: 33.2rem;
    height: 29.6rem; 
    padding: 1.6rem;
`;
const UpCalTitle = styled.div`
    width: 30rem;
    height: 2.4rem;
    background-color: ${COLORL.Primary};
    margin-bottom: 1.6rem;
    font-size: 1.6rem;
`;
const CalListBox = styled.div`
    width: 30rem;
    height: 22.4rem;
    // background-color: ${COLORL.Primary};
`;

//달력 리스트입니다.
const DayItemContainer = styled.li`
    display: inline-block;
    width: 4.286rem;
    height: 3.2rem;
    margin: 0.4rem 0;
    text-align: center;
    line-height: 3.2rem;
    font-size: 1.6rem;
    position: relative;
`;
//달력 list 선택 css 기능 
const ItemLeftBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${props => props.colorA};
    z-index: 10;
`;
const ItemRightBox = styled.div`
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    width: 2.143rem;
    height: 3.2rem;
    background-color: ${props => props.colorA};
    z-index: 10;
`;
const ItemCircle = styled.div`
    width: 3.2rem;
    height: 3.2rem;
    border-radius: 9999px;
    background-color: ${props => props.color};
    position: absolute;
    top: 0;
    left: 0.586rem;
    z-index: 11;
    overflow: hidden;
    cursor: pointer;

    &:hover {
        background-color: ${COLORL.Green};
    }
`;
const Span = styled.span`
    -webkit-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
`;
const Week = styled.div`
    width: 4.286rem;
    height: 2.4rem;
    display: inline-block;
    text-align: center;
`;
// const pickHandle = [];



//메인 
function CalendarBox () {
    const dataCal = new Date();
    const year = dataCal.getFullYear();
    const month = dataCal.getMonth();
    const totalMonthDay = new Date(year, month, 0).getDate(); // 1 ~ 31
    const firstDayWeek = new Date(year, month).getDay(); //0 ~ 6

    const [pickCon, setPickCon] = useState([]);

    function colorHandler ( value ) {
        let toggle = false;
        if (pickCon.length === 0) {
            for (let i = 0; i < pickCon.length ; i++) {
                if (pickCon[i] === value) {
                    toggle = true;
                };
            }
            if(!toggle) {
                setPickCon(pickCon => [...pickCon, value]);
            } else if (toggle) {
                toggle = false;
            }
        } else if (pickCon.length === 1) {
            for (let i = 0; i < pickCon.length ; i++) {
                if (pickCon[i] === value) {
                    toggle = true;
                };
            }
            if(!toggle) {
                setPickCon(pickCon => [...pickCon, value]);
            } else if (toggle) {
                setPickCon([]);
                toggle = false;
            }
            //선택일정 가운데 색상변경 토글


        } else if (pickCon.length === 2) {
            for(let i = 0; pickCon.length > i ; i++) {
                if (pickCon[i] === value){
                    setPickCon(pickCon => pickCon.splice(i,1));
                }
            }
        }
        console.log(pickCon);
    }


    function CalList ({ value, listColor, color, selecColor }) {
        let rightColor = 'white';
        let leftColor = 'white';
        if(selecColor === 'right') {
            leftColor = 'white';
            rightColor = 'black';
        } else if (selecColor === 'left'){
            rightColor = 'white';
            leftColor = 'black';
        } else if (selecColor === 'center') {
            rightColor = 'black';
            leftColor = 'black';
        }
        return (
            <>
                <DayItemContainer key={value} id={`li${value}`}>
                    <ItemLeftBox listColor={listColor} id={`left${value}`} colorA={leftColor}/>
                        <ItemCircle id={`cal${value}`} key={value} onClick={() => colorHandler(value)} color={color}>
                            <Span>{value}</Span>
                        </ItemCircle>
                    <ItemRightBox listColor={listColor} id={`right${value}`} colorA={rightColor}/>
                </DayItemContainer>
            </>
        )
    };

    const days = [];
    function renderList () {
        const selectorToggle =[];
        if (pickCon.length === 1) {
            selectorToggle.push(pickCon[0]);
        } else if (pickCon.length === 2) {
            selectorToggle.push(pickCon[0]);
            selectorToggle.push(pickCon[1]);
        }
        selectorToggle.sort(function(a, b)  {
            return a - b;
          });
        days.splice(0);
        for (let i = 0; i < firstDayWeek ; i++ ) {
            //빈칸 렌더링
            days.push(<CalList key={`empty${i}`} color={COLOR.Red}/>);
        }
        for (let i = 1; i <= totalMonthDay ; i++) {

            //day 렌더링
            if(selectorToggle[0] === i && selectorToggle.length === 2){
                days.push(<CalList key={i} value={i} color={COLOR.Green} selecColor={'right'} />)
            } else if (selectorToggle[1] === i && selectorToggle.length === 2) {
                days.push(<CalList key={i} value={i} color={COLOR.Green} selecColor={'left'} />)
            } else if (selectorToggle[0] < i && selectorToggle[1] > i && selectorToggle.length === 2) {
                days.push(<CalList key={i} value={i} color={COLOR.Red} selecColor={'center'}/>)
            } else {
                days.push(<CalList key={i} value={i} color={COLOR.Red}/>)
            }
        }
        console.log(selectorToggle);
        return days;
    }

    return (
        <CalendarContainer>
            <UpCalendar>
                <UpCalTitle>MAY 2023</UpCalTitle>
                <CalListBox>
                    <Week>S</Week>
                    <Week>M</Week>
                    <Week>T</Week>
                    <Week>W</Week>
                    <Week>T</Week>
                    <Week>F</Week>
                    <Week>S</Week>
                    {renderList()}
                </CalListBox>
            </UpCalendar>
        </CalendarContainer>
    )
}

export default CalendarBox;
