import { useState } from 'react';
import InputLogin from './InputLogin';
import './loginBox.css';
import LoginButton from './LoginButton';


const [cancelImgMove, setCancelImgMove] = useState(0);

function cancelImgFocus() {
    setCancelImgMove(1);
}

function LoginBox() {
    return (
        <div className="login-container">
            <h1>오늘의 나를 담아 내일로,</h1>
            <Logo />
            <InputLogin type='text' onFocus={cancelImgFocus} value={cancelImgMove}>아이디</InputLogin>
            <InputLogin type='password' onFocus={cancelImgFocus} value={cancelImgMove}>비밀번호</InputLogin>
            <LoginButton />
        </div>
    )
}

export default LoginBox;
