import InputLogin from './InputLogin';
import './loginBox.css';
import LoginButton from './LoginButton';
import Logo from './Logo';
import { useState } from 'react';


function LoginBox() {
    const [cancelImgMove, setCancelImgMove] = useState(0);

    function cancelImgFocus() {
        setCancelImgMove(1);
    }
    function cancelImgBlur() {
        setCancelImgMove(0);
    }

    return (
        <div className="login-container">
            <h1>오늘의 나를 담아 내일로,</h1>
            <Logo />
            <InputLogin type='text' onBlur={cancelImgBlur} onFocus={cancelImgFocus} value={cancelImgMove}>아이디</InputLogin>
            <InputLogin type='password' onFocus={cancelImgFocus} value={cancelImgMove}>비밀번호</InputLogin>
            <LoginButton />
        </div>
    )
}

export default LoginBox;
