import styled from "styled-components";
import COLOR from "./COLOR";

// const COLOR = {
//     Primary: '#5daece',
//     PrimaryLight: '#8cc9e1',
//     PrimaryDark: '3b85a3',
// }

const Container = styled.div`
    background-color: ${({ color }) => COLOR[color]};
    width: 20rem;
    height: 20rem;
`
export default Container;
