import { useNavigate } from 'react-router-dom';
import Button from './Button';
import InfoComment from './InfoComment';
import './Userinfo.css';
import catImg from '../../image/cat.png';
import StrongTest from './StrongTest';
import Challenge from './Challenge';
import styled from 'styled-components';
import ImgT from '../../image/mark_mark.png'
import COLOR from './COLOR';
import { useState } from 'react';

const infoName = '오주연/22207045';
const infoEmail = '@dongdongthecat';
const infoImg = catImg;

const RedDot = styled.div`
    border: 0.3rem solid ${COLOR.White};
    border-redius: 9999px;
    width: 1rem;
    height: 1rem;
    background-color: ${COLOR.Red};
    position: absolute;
    top: 1.8rem;
    right: 1.7rem;
`;
const ImageT = styled.img`
    position: absolute;
    width: 2.4rem;
    height: 2.4rem;
    top: 2rem;
    right: 1.7rem;
    cursor: pointer;
`;
function UserInfo({ commentPro }) {
    const imageA = ImgT;
    const [infoToggle, setInfoToggle] = useState(false);

    function commentClick() {
        setInfoToggle(true);
    }

    const movePage = useNavigate();
    function moveMypage () {
        movePage('/main/mypage');
    }

    return (
        <div className="userinfo-container container">
            <ImageT src={imageA} />
            <RedDot />
            <img src={infoImg} className='info-img' alt='프로필 사진' />
            <span>{infoName}</span>
            <span className='info-email'>{infoEmail}</span>
            <Button value={'마이페이지'} className={'infoMypage'} onClick={moveMypage}/>
            <InfoComment value={commentPro} toggle={infoToggle} onClick={commentClick}/>
            <StrongTest />
            <Challenge value={'Zzz'}/>
        </div>
    );
}

export default UserInfo;
