import styled from "styled-components";

const Input = styled.input`
width: 100%;
padding: 1.3rem;
height: 6rem;
font-size: 1.4rem;
border: 1px solid #8cc9e1;
border-radius: 0.8rem;
text-align: center;
&:focus {
    outline: none;
}
`;


function InfoCommentInput ({ value }) {

    const commentContent = `"${value}"`;
    return (
        <div className='info-comment-container'>
            <Input>{commentContent}</Input>
        </div>
    )
}

export default InfoCommentInput;
